var express = require('express'),
		path = require('path');

var app = express();

app.configure(function (){
	app.set('port', process.env.VCAP_APP_PORT || 3003);
	app.use(express.compress());
	app.use(app.router);
	app.use(express.static(path.join(__dirname, 'public')));
});

